import 'dart:async';import 'dart:convert';import 'package:auto_size_text/auto_size_text.dart';import 'package:cloud_firestore/cloud_firestore.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_launcher_icons/logger.dart';import 'package:icons_plus/icons_plus.dart';import 'package:japaneseapp/Config/FunctionService.dart';import 'package:japaneseapp/Config/dataHelper.dart';import 'package:japaneseapp/Screen/settingScreen.dart';import 'package:shared_preferences/shared_preferences.dart';class profileScreen extends StatefulWidget {  @override  State<StatefulWidget> createState() => _profileScreen();}class _profileScreen extends State<profileScreen> {  final List<String> weekdays = ["M", "T", "W", "T", "F", "S", "S"];  List<Map<String, String>> getWeekDays() {    DateTime today = DateTime.now();    int currentWeekday = today.weekday; // 1 = Th·ª© Hai, ..., 7 = Ch·ªß Nh·∫≠t    DateTime monday =        today.subtract(Duration(days: currentWeekday - 1)); // L√πi v·ªÅ Th·ª© Hai    List<Map<String, String>> days = [];    for (int i = 0; i < 7; i++) {      DateTime date = monday.add(Duration(days: i));      String formattedDate = date.day.toString().padLeft(2, '0');      String weekday = weekdays[i];      days.add({"date": formattedDate, "weekday": weekday});    }    return days;  }  List<Map<String, String>> getWeekDayMothYears() {    DateTime today = DateTime.now();    int currentWeekday = today.weekday; // 1 = Th·ª© Hai, ..., 7 = Ch·ªß Nh·∫≠t    DateTime monday =        today.subtract(Duration(days: currentWeekday - 1)); // L√πi v·ªÅ Th·ª© Hai    List<Map<String, String>> days = [];    for (int i = 0; i < 7; i++) {      DateTime date = monday.add(Duration(days: i));      String formattedDate =          "${date.day.toString().padLeft(2, '0')}/${date.month.toString().padLeft(2, '0')}/${date.year.toString()}";      String weekday = weekdays[i];      days.add({"date": formattedDate, "weekday": weekday});    }    print(days);    return days;  }  Future<Map<String, dynamic>> getData() async {    final SharedPreferences prefs = await SharedPreferences.getInstance();    final DatabaseHelper db = await DatabaseHelper.instance;    //FirebaseAuth.instance.signOut();    User user = FirebaseAuth.instance.currentUser!;    return {      "level": prefs.getInt("level"),      "exp": prefs.getInt("exp"),      "nextExp": prefs.getInt("nextExp"),      "Streak": prefs.getStringList("checkInHistoryTreak")!.length,      "lastCheckIn": prefs.getString("lastCheckIn"),      "checkInHistory": prefs.getStringList("checkInHistory"),      "checkInHistoryTreak": prefs.getStringList("checkInHistoryTreak"),      "achivement": prefs.getStringList("achivement"),      "providerID": user.providerData[0].providerId,      "displayName": user.providerData[0].displayName,      "mail": user.email,      "countTopic": (await db.getAllTopic()).length    };  }  String getTitle(int level) {    if (level < 10) {      return "H·ªçc Sƒ©";    } else if (level < 20) {      return "Minh Tri·∫øt Gi·∫£";    } else if (level < 30) {      return "T∆∞ T∆∞·ªüng Gia";    } else if (level < 40) {      return "B√°c H·ªçc T√¥n Gi·∫£";    } else if (level < 50) {      return "H√†n L√¢m H·ªçc Sƒ©";    } else if (level < 60) {      return "K·ª≥ T√†i VƒÉn H·ªçc";    } else if (level < 70) {      return "Tr√≠ Th√°nh Nh√¢n";    } else if (level < 80) {      return "Thi√™n T√†i Bi·ªán Lu·∫≠n";    } else if (level < 90) {      return "H·ªçc ƒê·∫°o Tinh Th√¥ng";    } else if (level < 100) {      return "Ch√≠ Sƒ© VƒÉn Nh√¢n";    } else if (level < 110) {      return "Luy·ªán T∆∞ H·ªçc Sƒ©";    } else if (level < 120) {      return "Th√¥ng Tu·ªá Gi·∫£";    } else if (level < 130) {      return "Hi·ªÅn Tri·∫øt Uy√™n Th√¢m";    } else if (level < 150) {      return "VƒÉn Nh√£ Ch√¢n Nh√¢n";    } else {      return "Tr√≠ Nh√¢n K·ª≥ T√†i";    }  }  String getImage(int level) {    if (level < 10) {      return "assets/StateLevel/state1.png";    } else if (level < 20) {      return "assets/StateLevel/state2.png";    } else if (level < 30) {      return "assets/StateLevel/state3.png";    } else if (level < 40) {      return "assets/StateLevel/state4.png";    } else if (level < 50) {      return "assets/StateLevel/state5.png";    } else if (level < 60) {      return "assets/StateLevel/state6.png";    } else if (level < 70) {      return "assets/StateLevel/state7.png";    } else if (level < 80) {      return "assets/StateLevel/state8.png";    } else if (level < 90) {      return "assets/StateLevel/state9.png";    } else if (level < 100) {      return "assets/StateLevel/state10.png";    } else if (level < 110) {      return "assets/StateLevel/state11.png";    } else if (level < 120) {      return "assets/StateLevel/state12.png";    } else if (level < 130) {      return "assets/StateLevel/state13.png";    } else if (level < 150) {      return "assets/StateLevel/state14.png";    } else {      return "assets/StateLevel/state15.png";    }  }  bool checkStreak(int userStreak, int streak){    return userStreak >= streak;  }  bool checkAchivement(Map<String, dynamic> data, String achivement){    return (data["achivement"] as List<String>).contains(achivement);  }  void showDialogSignOut(BuildContext ctx){    showDialog(      barrierDismissible: true,      context: context,      builder: (BuildContext context) {        return Dialog(          backgroundColor: Colors.white,          shape: const RoundedRectangleBorder(            borderRadius: BorderRadius.only(              bottomLeft: Radius.circular(10),              bottomRight: Radius.circular(10),            ), // Bo g√≥c popup          ),          child: StatefulBuilder(            builder: (BuildContext context, void Function(void Function()) setState) 