 today.subtract(Duration(days: currentWeekday - 1)); // L√πi v·ªÅ Th·ª© Hai    List<Map<String, String>> days = [];    for (int i = 0; i < 7; i++) {      DateTime date = monday.add(Duration(days: i));      String formattedDate =          "${date.day.toString().padLeft(2, '0')}/${date.month.toString().padLeft(2, '0')}/${date.year.toString()}";      String weekday = weekdays[i];      days.add({"date": formattedDate, "weekday": weekday});    }    print(days);    return days;  }  Future<Map<String, dynamic>> getData() async {    final SharedPreferences prefs = await SharedPreferences.getInstance();    final DatabaseHelper db = await DatabaseHelper.instance;    final DatabaseServer databaseServer = new DatabaseServer();    User user = FirebaseAuth.instance.currentUser!;    return {      "level": prefs.getInt("level"),      "exp": prefs.getInt("exp"),      "nextExp": prefs.getInt("nextExp"),      "Streak": prefs.getStringList("checkInHistoryTreak")!.length,      "lastCheckIn": prefs.getString("lastCheckIn"),      "checkInHistory": prefs.getStringList("checkInHistory"),      "checkInHistoryTreak": prefs.getStringList("checkInHistoryTreak"),      "achivement": prefs.getStringList("achivement"),      "providerID": user.providerData[0].providerId,      "displayName": user.providerData[0].displayName,      "mail": user.email,      "countTopic": (await db.getAllTopic()).length,      "coin": await databaseServer.getCoin(user.uid)    };  }  String getTitle(int level) {    if (level < 10) {      return AppLocalizations.of(context)!.levelTitles_1;    } else if (level < 20) {      return AppLocalizations.of(context)!.levelTitles_2;    } else if (level < 30) {      return AppLocalizations.of(context)!.levelTitles_3;    } else if (level < 40) {      return AppLocalizations.of(context)!.levelTitles_4;    } else if (level < 50) {      return AppLocalizations.of(context)!.levelTitles_5;    } else if (level < 60) {      return AppLocalizations.of(context)!.levelTitles_6;    } else if (level < 70) {      return AppLocalizations.of(context)!.levelTitles_7;    } else if (level < 80) {      return AppLocalizations.of(context)!.levelTitles_8;    } else if (level < 90) {      return AppLocalizations.of(context)!.levelTitles_9;    } else if (level < 100) {      return AppLocalizations.of(context)!.levelTitles_10;    } else if (level < 110) {      return AppLocalizations.of(context)!.levelTitles_11;    } else if (level < 120) {      return AppLocalizations.of(context)!.levelTitles_12;    } else if (level < 130) {      return AppLocalizations.of(context)!.levelTitles_13;    } else if (level < 150) {      return AppLocalizations.of(context)!.levelTitles_14;    } else {      return AppLocalizations.of(context)!.levelTitles_15;    }  }  String getImage(int level) {    if (level < 10) {      return "assets/StateLevel/state1.png";    } else if (level < 20) {      return "assets/StateLevel/state3.png";    } else if (level < 30) {      return "assets/StateLevel/state3.png";    } else if (level < 40) {      return "assets/StateLevel/state4.png";    } else if (level < 50) {      return "assets/StateLevel/state5.png";    } else if (level < 60) {      return "assets/StateLevel/state6.png";    } else if (level < 70) {      return "assets/StateLevel/state7.png";    } else if (level < 80) {      return "assets/StateLevel/state8.png";    } else if (level < 90) {      return "assets/StateLevel/state9.png";    } else if (level < 100) {      return "assets/StateLevel/state10.png";    } else if (level < 110) {      return "assets/StateLevel/state11.png";    } else if (level < 120) {      return "assets/StateLevel/state12.png";    } else if (level < 130) {      return "assets/StateLevel/state13.png";    } else if (level < 150) {      return "assets/StateLevel/state14.png";    } else {      return "assets/StateLevel/state15.png";    }  }  bool checkStreak(int userStreak, int streak){    return userStreak >= streak;  }  @override  Widget build(BuildContext context) {    List<Map<String, String>> days = getWeekDays();    List<Map<String, String>> daymonthYears = getWeekDayMothYears();    DateTime today = DateTime.now();    String todayFormatted = "${today.day.toString().padLeft(2, '0')}";    return WillPopScope(child: Scaffold(        body: FutureBuilder(future: getData(), builder: (context, snapshot) {          if (snapshot.connectionState == ConnectionState.waiting) {            return Center(              child: Container(                height: 100,                width: 100,                child: const CircularProgressIndicator(                  color: Colors.green,                ),              ),            );          }          if (!snapshot.hasData) {            return Center(              child: Container(                  height: MediaQuery.sizeOf(context).width * 0.8,                  child: Image.asset("assets/404.png")),            );          }          return Container(            width: MediaQuery.sizeOf(context).width,            height: MediaQuery.sizeOf(context).height,            color: Color(0xFFFFA2B1),            child: Stack(              children: [                Container(                  width: MediaQuery.sizeOf(context).width,                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.end,                    children: [                      SizedBox(height: 100,),                      Image.asset(                        getImage(snapshot.data!["level"] ?? 1),                        width: MediaQuery.sizeOf(context).width/2,                        fit: BoxFit.fitWidth,                      ),                    ],                  ),                ),                Container(                  width: MediaQuery.sizeOf(context).width,                  height: MediaQuery.sizeOf(context).height/2.5,                  child: C