ch synchrony data from local database    List<String> dataAsynchronyData = [];    dataAsynchronyData.add((await db.getAllSynchronyData()));    dataAsynchronyData.add(jsonEncode(dataPrefs));    try {      // Get Firestore instance      var userDoc = FirebaseFirestore.instance.collection("datas").doc(FirebaseAuth.instance.currentUser!.uid);      // Check if the document already exists      var docSnapshot = await userDoc.get();      if (docSnapshot.exists) {        // If data already exists, clear it first        await userDoc.update({          'data': FieldValue.delete(), // This deletes the existing 'data' field        });        print("Existing data cleared");      }      // Now write the new data      await userDoc.set({        'data': dataAsynchronyData      }, SetOptions(merge: true)); // This ensures that existing fields are not removed, just updated.      setState(() {        isLoading = false;      });      showDialogPushDataSuccess(context);    } catch (e) {      print("Error synchronizing data: $e");    }  }  Future<void> UpdateAsynchronyData() async {    DatabaseHelper db = DatabaseHelper.instance;    final SharedPreferences prefs = await SharedPreferences.getInstance();    setState(() {      isLoading = true;    });    try {      // Get the Firestore instance      var userDoc = FirebaseFirestore.instance.collection("datas").doc(FirebaseAuth.instance.currentUser!.uid);      // Get the document snapshot      var docSnapshot = await userDoc.get();      // Check if the document exists      if (docSnapshot.exists) {        // Document exists, retrieve the data        var data = docSnapshot.data(); // This will return a Map<String, dynamic> with the document data        String dataInDatabase = data!["data"][0];        await db.importSynchronyData(dataInDatabase);        Map<String, dynamic> dataPrefs = jsonDecode(data["data"][1]);        print(dataPrefs);        await prefs.clear();        await prefs.setInt("level", dataPrefs["level"]);        await prefs.setInt("exp", dataPrefs["exp"]);        await prefs.setInt("nextExp", dataPrefs["nextExp"]);        await prefs.setInt("Streak", dataPrefs["Streak"]);        await prefs.setString("lastCheckIn", dataPrefs["lastCheckIn"]);        await prefs.setStringList("checkInHistory", _convertToListString(dataPrefs["checkInHistory"]));        await prefs.setStringList("checkInHistoryTreak", _convertToListString(dataPrefs["checkInHistoryTreak"]));        await prefs.setStringList("achivement", _convertToListString(dataPrefs["achivement"]));        userDoc.delete();        showDialogAsynchronySuccess(context);      } else {        showDialogDownloadDataFail(context);      }    } catch (e) {      print("Error retrieving data: $e");    }    setState(() {      isLoading = false;    });  }  List<String> _convertToListString(dynamic list) {    if (list == null) return [];    return List<String>.from(list.map((item) => item.toString()));  }  void showDialogAchivementInfor(BuildContext ctx, String title, String description, String image){    showDialog(      barrierDismissible: true,      context: context,      builder: (BuildContext context) {        return Dialog(          backgroundColor: Colors.white,          shape: const RoundedRectangleBorder(            borderRadius: BorderRadius.only(              bottomLeft: Radius.circular(10),              bottomRight: Radius.circular(10),            ), // Bo g√≥c popup          ),          child: StatefulBuilder(            builder: (BuildContext context, void Function(void Function()) setState) {              return Container(                width: MediaQuery.sizeOf(context).width*0.8,                decoration: const BoxDecoration(                  border: Border(                    top: BorderSide(                      color: Color.fromRGBO(20, 195, 142, 1.0), // M√†u xanh c·∫°nh tr√™n ngo√†i c√πng                      width: 10.0, //